CREATE OR ALTER PROCEDURE Audit.mig_recon_stg_db_load_proc
    @pipeline varchar(1000),
    @filename varchar(1000),
    @rowsread INT,
    @rowscopied INT,
    @copyduration INT,
    @metric varchar(1000)
AS
BEGIN
    insert into Audit.mig_recon_stg_db_load (pipeline, filename, rowsread, rowscopied, copyduration, metric) values (@pipeline,@filename, @rowsread, @rowscopied, @copyduration, @metric )
END;
