CREATE OR ALTER PROCEDURE Audit.mig_recon_stg_db_load_proc
    @pipeline varchar(1000),
    @filename varchar(1000),
    @rowsread INT,
    @rowscopied INT,
    @copyduration INT,
    @metric varchar(1000)
AS
BEGIN
    insert into Audit.mig_recon_stg_db_load (pipeline, filename, rowsread, rowscopied, copyduration, metric) values (@pipeline,@filename, @rowsread, @rowscopied, @copyduration, @metric )
END;

CREATE OR ALTER PROCEDURE [SF_Archive].[ColsWithoutSystemCols]
    @TableName varchar(max)
AS
BEGIN
	declare @cols varchar(max), @query varchar(max);
	SELECT  @cols = STUFF
    (
        ( 
            SELECT DISTINCT '], [' + name
            FROM sys.columns
            where object_id = (
                select top 1 object_id from sys.objects
                where name = @TableName 
            )
            and name not in ('Id', 'IsDeleted', 'SetupOwnerId', 'CreatedById','LastModifiedById','SystemModstamp')
            FOR XML PATH('')
        ), 1, 2, ''
    ) + ']';
SELECT @query = 'select ' + @cols + ' from [SF_Archive].' + '[' + @TableName + '] where IsDeleted = 0';
EXEC (@query);
END;
