CREATE OR ALTER PROCEDURE [SF_Archive].[proc_sf_sql_extract]
    @ObjectName varchar(max)
AS
BEGIN
declare @query varchar(max);
set @query = (select top 1 sql from audit.mig_objects_list where objectname='' + @ObjectName + '' and enabled=1);

exec (@query);
END
